import React, { Component } from "react";
import { connect } from "react-redux";
import { getTransactions } from "../actions/transactionActions";
import {Container} from "reactstrap"
import PropTypes from "prop-types";
import { Pie } from "react-chartjs-2";

class ChartData extends Component{

    data ={
        labels: [],
        datasets: [{
            label: '# of Votes',
            data: [],
            backgroundColor: [
                'rgba(86, 0, 145, 0.7)',
                'rgba(86, 197, 145, 0.7)',
                'rgba(86, 108, 255, 0.7)',
                'rgba(242, 108, 255, 0.7)'
            ],
            borderColor: [
                'white'
            ],
            borderWidth: 2
        }]
    }

    setGradientColor = (canvas, color) =>{
        const ctx = canvas.getContext('2d');
        const gradient = ctx.createLinearGradient(0,0,600,550)
        gradient.addColorStop(0, color);
        gradient.addColorStop(0.95, "rgba(133,122,144,0.)");
        return gradient
    }

    setChartBgColor = canvas => {
        if (this.data.datasets){
            let colors = ["rgbs(255, 0, 255, 0.75)", "rgba(0, 255, 0, 0,75)"];
            this.data.datasets[0].data.foreach((set, i) => {
                this.data.datasets[0].backgroundColor= this.setGradientColor(canvas, colors[i]);
            })
        }
    }


    render(){
        const { transactions } = this.props.transaction
        const chartLabels=[];
        transactions.map(({title, amount})=>{
            if (amount<0){
                chartLabels.push(title)
            }

        });


        const chartData=[];
        transactions.map(({amount})=>{
            if (amount <0){
                chartData.push(amount)
            }
        });

        this.data.datasets[0].data=chartData
        this.data.labels=chartLabels


        return(
            <Container>
                <Pie data={this.data} responsive="true" />
            </Container>
        )
    }
}

ChartData.propTypes = {
    getTransactions: PropTypes.func.isRequired,
    transaction: PropTypes.object.isRequired,
    getStats: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
    transaction: state.transaction

})

export default connect(mapStateToProps, { getTransactions })(ChartData)
