import React, { Component } from "react";
import { Container, ListGroup, ListGroupItem, Button } from "reactstrap";
import { CSSTransition, TransitionGroup } from "react-transition-group"
import uuid from 'uuid/v1'
import { FaMinus } from 'react-icons/fa';
import { connect } from "react-redux"
import { getTransactions, deleteTransaction } from "../actions/transactionActions"
import PropTypes from "prop-types"

class TransactionList extends Component{

    componentDidMount(){
        this.props.getTransactions();
    }

    onDeleteClick = (id) =>{
        this.props.deleteTransaction(id);
    }




    render() {
        const { transactions } = this.props.transaction
        return(
            <Container>
                {transactions.map(( {id,title,amount,date}) => (
                    <CSSTransition key={id} timeout={500} classNames="fade">
                        <div className="transaction-item">
                            <Button 
                                className="remove-button"
                                color="danger" 
                                size="sm"
                                onClick={this.onDeleteClick.bind(this, id)}>
                                &times;
                            </Button>
                            <div className="transaction-list-title">
                                {title}
                            </div>
                            <div className="transaction-list-amount">
                                {amount}
                            </div>
                            <div className="transaction-list-date">
                                {Date.now()}
                            </div>
                            <div className="neg" />

                        </div>

                    </CSSTransition>
                ))}


            </Container>
        )
    }

}

TransactionList.propTypes = {
    getTransactions: PropTypes.func.isRequired,
    transaction: PropTypes.object.isRequired,
    income: PropTypes.number,
    amount: PropTypes.number
}

const mapStateToProps = (state) => ({
    transaction: state.transaction,
    income: state.income
})


export default connect(mapStateToProps, {getTransactions, deleteTransaction})(TransactionList);
