import React, { Component } from "react";
import { Container } from "reactstrap"
import { connect } from "react-redux";
import { updateStats } from "../actions/transactionActions"
import PropTypes from "prop-types"

class TransactionStats extends Component {
    render(){
        const { income }= this.props.income
        return(
            <Container>
                <div className="stats-wrapper">
                    <div className="stats-income">
                        {income}
                    </div>
                    <div className="stats-remainder">
                        $0.00
                    </div>
                </div>
            </Container>
        )
    }
}



TransactionStats.propTypes = {
    getTransactions: PropTypes.func.isRequired,
    transaction: PropTypes.object.isRequired,
    income: PropTypes.number,
    remainder: PropTypes.number

}

const mapStateToProps = state => ({
    transaction: state.transaction,

})

export default connect(mapStateToProps, { updateStats })(TransactionStats)
