import uuid from 'uuid/v1'
import { GET_TRANSACTIONS, ADD_TRANSACTION, DELETE_TRANSACTION, UPDATE_STATS } from "../actions/types"

const initialState ={
    transactions: [
        {
            id: uuid(),
            title: "Sainsbury's",
            amount: -33.75,
            date: Date.now()
        },
        {
            id: uuid(),
            title: "Greggs",
            amount: -15.25,
            date:Date.now()
        },
        {
            id: uuid(),
            title: "Mission Burrito",
            amount: -7.23,
            date:Date.now()
        },
        {
            id: uuid(),
            title: "Fishies",
            amount: 100,
            date:Date.now()
        },
    ],
    income:10,
    remainder: 0
}

export default function(state=initialState, action){
    switch(action.type){
        case GET_TRANSACTIONS:
            return{
                ...state
            }
        case DELETE_TRANSACTION:
            return{
                ...state,
                transactions: state.transactions.filter(transaction => transaction.id !== action.payload)
            }
        case ADD_TRANSACTION:
            return{
                ...state,
                transactions: [action.payload, ...state.transactions]
            }
        case UPDATE_STATS:
            action.payload=parseInt(action.payload)
            if (action.payload>0){
                state.income += action.payload
            }
            else{
                state.remainder = state.income - action.payload
            }
        default:
            return state;
    }
}
