import React, { Component } from "react";
import { Container, ListGroup, ListGroupItem, Button } from "reactstrap";
import { CSSTransition, TransitionGroup } from "react-transition-group"
import uuid from 'uuid/v1'

class TransactionList extends Component{
    state={
        transactions: [
            {
                id: uuid(),
                title: "Sainsbury's",
                amount: -33.75,
                date:Date.now()
            },
            {
                id: uuid(),
                title: "Greggs",
                amount: -15.25,
                date:Date.now()
            },
            {
                id: uuid(),
                title: "Mission Burrito",
                amount: -7.23,
                date:Date.now()
            },
            {
                id: uuid(),
                title: "Fishies",
                amount: -100,
                date:Date.now()
            },
        ]
    }




    render() {
        const { transactions } = this.state
        return(
            <Container>
                <Button color="primary" style={{marginBottom:'2rem'}} onClick={() => {
                    const title=prompt('Enter transaction title')
                    const amount=parseInt(prompt("Enter Transaction Amount"))
                    if (title && amount){
                        this.setState(state => ({
                            transactions: [...state.transactions, { id: uuid(), title, amount }]
                        }))
                    }
                }}>Add Transaction</Button>
                <br />
                {transactions.map(( {id,title,amount,date}) => (
                    <CSSTransition key={id} timeout={500} classNames="fade">
                        <Button 
                        className="remove-button"
                            color="danger" 
                            size="sm"
                            onClick={() => {
                                this.setState(state => ({
                                    transactions: state.transactions.filter(transaction => transaction.id !== id)
                                }))
                            }}>
                            &times;
                        </Button>
                    </CSSTransition>
                ))}


            </Container>
        )
    }

}

export default TransactionList;
