import React, { Component } from "react";
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    Form,
    FormGroup,
    Label,
    Input
} from "reactstrap";
import { connect } from "react-redux";
import { addTransaction } from "../actions/transactionActions"
import uuid from 'uuid/v1'

class TransactionModal extends Component{
    state ={
        modal:false,
        title: '',
        amount:0
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal
        })
    }

    onChange = (e) => {
        this.setState({ [e.target.title]:e.target.value })
        this.setState({ [e.target.amount]:e.target.value })
    }

    onSubmitAdd = e => {
        e.preventDefault();

        const newTransaction = {
            id: uuid(),
            title: this.state.title,
            amount: this.state.amount
        }

        this.props.addTransaction(newTransaction);

        this.toggle();
    }


    render(){
        return(
            <div>
                <Button className="addtransButton" onClick={this.toggle}>
                    Add Transaction
                </Button>
                <Modal
                isOpen={this.state.modal}
                toggle={this.toggle}>
                    <ModalHeader toggle={this.toggle}>
                        Add a Transaction!
                    </ModalHeader>
                    <ModalBody>
                        <Form onSubmit={this.onSubmitAdd}>
                            <FormGroup>
                                <Input 
                                type="text"
                                name="title"
                                id="transaction"
                                placeholder="Enter Transaction Title"
                                onChange={this.onChange}
                                />
                            </FormGroup>
                            <FormGroup>
                                <Input 
                                type="text"
                                name="title"
                                id="transaction"
                                placeholder="Enter Transaction Amount (Start with - If Negative)"
                                onChange={this.onChange}
                                />
                            </FormGroup>
                            <Button className="addTransButtonModal">
                                Add Transaction
                            </Button>
                        </Form>
                    </ModalBody>
                </Modal>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    transaction: state.transaction
})

export default connect(mapStateToProps, { addTransaction })(TransactionModal)
