import React, { Component, useState } from "react";
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    Label,
    Input,
    Container
} from "reactstrap";
import {v1 as uuid} from "uuid"; 
import { connect } from "react-redux";
import {addTransaction} from "../actions/transactionActions"

class TransactionModal extends Component{
    today=new Date
    theDate=this.today.getDate()+'/'+(this.today.getMonth()+1)+'/'+this.today.getFullYear();

    state = {
        modal: false,
        title:'',
        amount: 0,
        date: this.theDate
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal
        });
    }

    onChange = (e) =>{
        this.setState({ [e.target.name]: e.target.value })
        this.setState({ [e.target.debit]: e.target.value })
        this.setState({ [e.target.date]: Date.now() })
    }

    onSubmit = e => {
        e.preventDefault();
        const newTransaction = {
            id: uuid(),
            name: this.state.name,
            debit: this.state.debit,
            date: this.state.date
        }

        this.props.addTransaction(newTransaction);

        this.toggle();
    }




    render() {
        return(
            <Container>
                <Button
                    className="addTransactionButton"
                    onClick={this.toggle}>
                    Add Item
                </Button>

                <Modal isOpen={this.state.modal} toggle={this.toggle}>

                    <ModalHeader toggle={this.toggle}>Add To Transaction List</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={this.onSubmit}>
                            <FormGroup>
                                <Input 
                                    type="text"
                                    name="name"
                                    id="transaction"
                                    placeholder="Add Transaction Here"
                                    onChange={this.onChange}
                                    />
                                <br></br>
                                <Input 
                                    type="text"
                                    name="debit"
                                    id="transaction"
                                    placeholder="Add Debit Amount Here"
                                    onChange={this.onChange}
                                    />
                                <Button block>Add Transaction</Button>
                            </FormGroup>
                        </Form>
                    </ModalBody>
                </Modal>
            </Container>
        );
    }


}
const mapStateToProps = state => ({
    transaction: state.transaction
})

export default connect(mapStateToProps, { addTransaction })(TransactionModal);
